mapper.select:

SELECT * 
FROM sys_login_log a
WHERE 1 = 1

	AND a.userId = #{userId}


	and (a.loginTime between #{startTime} and #{endTime}) 


	and a.loginTime >= #{startTime}


	and a.loginTime <= #{endTime}

ORDER BY a.loginTime DESC

mapper.delete:
DELETE FROM sys_login_log


		WHERE 1 != 1


		WHERE loginTime >= #{startTime}
